mapper.select:
select
    a.id, a.box_no, a.order_id, a.orderdetail_id, a.distributionserial_id, a.invoice_no, a.platform_drug_code, a.procurecatalog_id, a.province_id, a.drug_name, a.drug_type, a.drug_specs, a.form_name, a.production_code, a.production_name, a.license_no, a.approval_no, a.valid_date, a.order_count, a.distribute_count, a.unit, a.distribute_time, a.price, a.amount, a.supplier_code, a.supplier_name, a.supplier_drug_code, a.is_expensive, a.drug_check_status, a.drug_check_datetime, a.check_count, a.check_user_id, a.delete_flag, a.create_by, a.create_datetime, a.update_by, a.update_datetime, a.pieces, a.distribute_code, a.ajust_count
from scm_his_unsynchronized_delivery_order_details a

    a.delete_flag=0

        and a.id = #{id}


        and a.box_no = #{boxNo}


        and a.order_id = #{orderId}


        and a.orderdetail_id = #{orderdetailId}


        and a.distributionserial_id = #{distributionserialId}


        and a.invoice_no = #{invoiceNo}


        and a.platform_drug_code = #{platformDrugCode}


        and a.procurecatalog_id = #{procurecatalogId}


        and a.province_id = #{provinceId}


        and a.drug_name = #{drugName}


        and a.drug_type = #{drugType}


        and a.drug_specs = #{drugSpecs}


        and a.form_name = #{formName}


        and a.production_code = #{productionCode}


        and a.production_name = #{productionName}


        and a.license_no = #{licenseNo}


        and a.approval_no = #{approvalNo}


        and a.valid_date = #{validDate}


        and a.order_count = #{orderCount}


        and a.distribute_count = #{distributeCount}


        and a.unit = #{unit}


        and a.distribute_time = #{distributeTime}


        and a.price = #{price}


        and a.amount = #{amount}


        and a.supplier_code = #{supplierCode}


        and a.supplier_name = #{supplierName}


        and a.supplier_drug_code = #{supplierDrugCode}


        and a.is_expensive = #{isExpensive}


        and a.drug_check_status = #{drugCheckStatus}


        and a.drug_check_datetime = #{drugCheckDatetime}


        and a.check_count = #{checkCount}


        and a.check_user_id = #{checkUserId}


        and a.create_by = #{createBy}


        and a.create_datetime = #{createDatetime}


        and a.update_by = #{updateBy}


        and a.update_datetime = #{updateDatetime}


        and a.pieces = #{pieces}


        and a.distribute_code = #{distributeCode}


        and a.ajust_count = #{ajustCount}
