mapper.select:
select
    a.user_id, a.region_value, a.delete_flag, a.create_by, a.create_datetime, a.update_by, a.update_datetime
from sys_user_region a

    a.delete_flag=0

        and a.user_id = #{userId}


        and a.region_value = #{regionValue}


        and a.create_by = #{createBy}


        and a.create_datetime = #{createDatetime}


        and a.update_by = #{updateBy}


        and a.update_datetime = #{updateDatetime}
